package net.erchen.adventofcode2022.day06;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;

import static org.assertj.core.api.Assertions.assertThat;

class SignalTest {

    @CsvSource({
            "mjqjpqmgbljsphdztnvjfqwrcgsmlb,7",
            "bvwbjplbgvbhsrlpgdmjqwftvncz,5",
            "nppdvjthqldpwncqszvftbrmjlhg,6",
            "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg,10",
            "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw,11",
            ",1538"
    })
    @ParameterizedTest
    void shouldFindStarterMarker(String input, int expectedPaketMarker) {
        assertThat(new Signal(input).paketMarker()).isEqualTo(expectedPaketMarker);
    }

    @CsvSource({
            "mjqjpqmgbljsphdztnvjfqwrcgsmlb,19",
            "bvwbjplbgvbhsrlpgdmjqwftvncz,23",
            "nppdvjthqldpwncqszvftbrmjlhg,23",
            "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg,29",
            "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw,26",
            ",2315"
    })
    @ParameterizedTest
    void shouldFindMessageMarker(String input, int expectedMessageMarker) {
        assertThat(new Signal(input).messageMarker()).isEqualTo(expectedMessageMarker);
    }
}